import {useState} from 'react'
import './StepOne.css';
import { Form, Card, Button } from "react-bootstrap";
import validator from "validator";
 function StepOne({nextStep ,handleFormData, values}) {



  let [state, setstate] = useState(1)
  const nextState= ()=>{
        setstate (state +1);
        console.log(state)
  }
  

    const [error, setError] = useState(false);
   
    const submitFormData = (e) => {
        e.preventDefault();
    
         // checking if value of first name and last name is empty show error else take to next step
        if (validator.isEmpty(values.email) || validator.isEmpty(values.username) ||validator.isEmpty(values.password) || validator.isEmpty(values.confirmpassword)) {
          setError(true);
        }
    
         else {
          nextStep();
        }
      };
  return (
    <>
        <div className='main_container'>
        <p className='text'>SIGN UP YOUR ACCOUNT <span> fill all form field to go to next step</span></p>
            <div className='form_steps'>
                <ul>
                    <li>Account </li>
                    <li>Personal </li>
                    <li>Image </li>
                    <li>Finish </li>

                </ul>
            </div>
       <form onSubmit={submitFormData} >
       <h2> Account Information</h2>
       <p>{state} -4 step</p>
        <div className='fields'>
            <label> Email:*</label>
            <input 
            type="email"
            name='email'
            defaultValue={values.email}
            placeholder='Enter your Email'
            onChange={handleFormData("email")}
            />
            {error ? (
                <Form.Text style={{ color: "red" }}>
                  This is a required field
                </Form.Text>
              ) : (
                " "
              )}
        </div>
        <div className='fields'>
            <label> User Name:*</label>
            <input 
            type="text"
            name='username'
            defaultValue={values.username}
            placeholder='Enter your User Name'
            onChange={handleFormData("username")}
            />
            {error ? (
                <Form.Text style={{ color: "red" }}>
                  This is a required field
                </Form.Text>
              ) : (
                ""
              )}
        </div>
        <div className='fields'>
            <label> Password:*</label>
            <input 
            type="password"
            name='password'
            defaultValue={values.password}
            placeholder='Enter your password'
            onChange={handleFormData("password")}
            />
             {error ? (
                <Form.Text style={{ color: "red" }}>
                  This is a required field
                </Form.Text>
              ) : (
                ""
              )}
        </div>
        <div className='fields'>
            <label> Confirm Password:*</label>
            <input 
            type="password"
            name='confirmpassword'
            defaultValue={values.confirmpassword}
            placeholder='Enter your password'
            onChange={handleFormData("confirmpassword")}
            />
            {error ? (
                <Form.Text style={{ color: "red" }}>
                  This is a required field
                </Form.Text>
              ) : (
                ""
              )}
        </div>
        <div className='button'>
        <button type='submit'  className='next_btn btn1 ' > Next</button>
        <button onClick={nextState}> sate</button>
            
        </div>
       </form>
       </div>
    </>
  )
}
export default StepOne
