import { useState } from "react";
import { Form, Card, Button } from "react-bootstrap";
import validator from "validator";

function StepTwo({ nextStep, handleFormData2, values, prevStep }) {
  const [error, setError] = useState(false);
// console.log(nextStep);
  const submitFormData2 = (e) => {
    e.preventDefault();
    // console.log(nextStep);
   console.log(validator.isEmpty(values.firstName))
  
    // checking if value of first name and last name is empty show error else take to next step
    if (
      validator.isEmpty(values.firstName) ||
      
      validator.isEmpty(values.lastName) ||
      validator.isEmpty(values.contact) ||
      validator.isEmpty(values.altenatecontact)) {
         setError(true);
    } else {
      nextStep();
    }

  };
  return (
    <>
      <div className="main_container">
        <p className="text">
          SIGN UP YOUR ACCOUNT
          <span> fill all form field to go to next step</span>
        </p>
        <div className="form_steps">
          <ul>
            <li>Account </li>
            <li>Personal </li>
            <li>Image </li>
            <li>Finish </li>
          </ul>
        </div>
        <form onSubmit={submitFormData2}>
        <div className='heading'>
          <h2> Prsonal Information</h2>
          <p>2-4 step</p>
          </div>
          <div className="fields">
            <label> First Name:*</label>
            <input
              type="text"
              name="firstName"
              defaultValue={values.firstName}
              placeholder="Enter your first name"
              onChange={handleFormData2("firstName")}
            />
            {error ? (
              <Form.Text style={{ color: "red" }}>
                This is a required field
              </Form.Text>
            ) : (
              " "
            )}
          </div>
          <div className="fields">
            <label> Last Name:*</label>
            <input
              type="text"
              name="lastName"
              defaultValue={values.lastName}
              placeholder="Enter your last Name"
              onChange={handleFormData2("lastName")}
            />
            {error ? (
              <Form.Text style={{ color: "red" }}>
                This is a required field
              </Form.Text>
            ) : (
              " "
            )}
          </div>
          <div className="fields">
            <label> Contact:*</label>
            <input
              type="number"
              name="contact"
              defaultValue={values.contact}
              placeholder="Enter your contact"
              onChange={handleFormData2("contact")}
            />
            {error ? (
              <Form.Text style={{ color: "red" }}>
                This is a required field
              </Form.Text>
            ) : (
              " "
            )}
          </div>
          <div className="fields">
            <label> altenate contact:*</label>
            <input
              type="number"
              name="altenatecontact"
              defaultValue={values.altenatecontact}
              placeholder="Enter your altenate contact"
              onChange={handleFormData2("altenatecontact")}
            />
            {error ? (
              <Form.Text style={{ color: "red" }}>
                This is a required field
              </Form.Text>
            ) : (
              " "
            )}
          </div>
          <div className="button">
            <button type="submit" className="next_btn  btn1" onClick={nextStep} > Next </button>
            <button className="next_btn   btn2 "  onClick={prevStep}>  previous  </button>
          </div>
        </form>
      </div>
    </>
  );
}
export default StepTwo;
