import { useState, useEffect   } from "react";

import Image_Upload from "./Image_Upload";
import Personal_Information from "./Personal_Information";
import SignUp_Result from "./SignUp_Result";
import "./Sign_Account.css";

function Sign_Account() {
  const initialValues = {
    Email: "",
    username: "",
    Password: "",
    Confirmpasswod: "",
  };
  const [formValues, setFormValues] = useState(initialValues);
  const [formErrors, setFormErrors] = useState({});
  const [isSubmit , setIsSubmint] = useState(false);
  const stepinitialValue = {number:1};
  const [stepUpdate, setStepUpdate] = useState(stepinitialValue)


  const handleChange = (e) => {
    // console.log(e)
    const { name, value } = e.target;  
    setFormValues({...formValues, [name]: value}  );
    // console.log(setFormValues);
    // console.log(formValues);
  };
  const handleSubmit =(e)=>{
      e.preventDefault();
      setFormErrors(validate(formValues))
      setIsSubmint(true);
      const number=0
      setStepUpdate (stepUpdate, number+1)
      console.log( setStepUpdate)
      
  }
  useEffect (() =>{
    // console.log (formErrors)
      if (Object.keys(formErrors).length === 0 &&  isSubmit ){
        
        console.log (stepUpdate)
      }
  },[formErrors])
  
  const validate =(values)=>{
      const errors ={};
      const regex =  /^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/
      if (!values.Email){
        errors.Email = " Email is required ! "
      }else if (!regex.test(values.Email)){
        errors.Email = "this is not a valid email format"
      }
      if (!values.username){
        errors.username = " User Name is required ! "
      }
      if (!values.Password){
        errors.Password = " Password is required ! "
      }
      if (!values.Confirmpasswod){
        errors.Confirmpasswod = " Confirm password is required ! "
      }else if (values.Password != values.Confirmpasswod){
        errors.Confirmpasswod = " Confirm password is not same !"
      }
       return errors; 
  }

  return (
    <>
      <div className="SignUp_Account">
        <p className="SignUp_Account_text">
          SIGN UP YOUR ACCOUNT
          <span> Fill all form feild to go to next step</span>
        </p>
        <ul className="UserAccount_step">
          <li> Account</li>
          <li> Personal</li>
          <li> Image</li>
          <li>finish</li>
        </ul>
        <form className="Account_Information"  onSubmit={handleSubmit} >
        <pre>{JSON.stringify(formValues, undefined, 2 )} </pre>
          <div className="account_information_style">
            <heading> Account Information :</heading>
  
            <div className="steps"> step {stepinitialValue }-4 </div>
          </div>

          <label>Email :*</label>
          <input
            type="email"
            name="Email"
            placeholder=" Email Id"
            id="email "
            value={formValues.Email}
            onChange={handleChange}
          />
          <p className="errors">{formErrors.Email} </p>
          <label>User Name :*</label>
          <input
            type="text"
            placeholder="User Name "
            name="username"
            id="username"
            value={formValues.username}
            onChange={handleChange}
          />
          <p className="errors">{formErrors.username} </p>

          <label>Password :*</label>
          <input
            type="password"
            placeholder="password "
            name="Password"
            id="password"
            value={formValues.Password}
            onChange={handleChange}
          />
          <p className="errors">{formErrors.Password} </p>

          <label>Confirm Password:*</label>
          <input
            type="password"
            placeholder="Confirm Password "
            name="Confirmpasswod"
            id="confirmPassword"
            value={formValues.Confirmpasswod}
            onChange={handleChange}
          />
          <p className="errors">{formErrors.Confirmpasswod} </p>

        <div class="next_btn">
          {/* <button class="btn1 btn-primry">Previos</button> */}
          <button class="btn2 btn-primry" >Next </button>
        </div>
        {/* <Personal_Information />
      <Image_Upload />
      <SignUp_Result /> */}
      </form>

      </div>
      
    </>
  );
}
export default Sign_Account;
