import React, { useState } from "react";

import Image_Upload from "./Image_Upload";
import Personal_Information from "./Personal_Information";
import SignUp_Result from "./SignUp_Result";
import "./Sign_Account.css";


function Sign_Account() {
  // var step1 = 2;
  const initialValue = {
    email: "",
    username: "",
    password: "",
    confirmpassword: "",
  };
  const [formValues, setFormValues] = useState(initialValue);

  const handelChange = (e) => {
    // console.log(e.target)
    const { name, value } = e.target;
    setFormValues({ ...formValues, [name]: value });
    console.log(formValues);
  };
  
        var stepChange = stepconter;
  const Next_step= () =>{
        var  stepconter = 0;  
        stepconter++;
        // const { value } = e.target;
        // console.log(value);
        // console.log("aqib");
        // let firststep = 1;
        // let laststep = 4;
        // let step = `<p>1 of 4 step</p>`;
        // console.log(step)


  }
  
  
  
  
  return (
    <>
      <div className="SignUp_Account">
        <p>
         
          SIGN UP YOUR ACCOUNT
          <span> Fill all form feild to go to next step</span>
        </p>
        <ul className="UserAccount_step">
          <li> Account</li>
          <li> Personal</li>
          <li> Image</li>
          <li>finish</li>
        </ul>
        <form className="Account_Information">
        <div className="account_information_style">
        <heading> Account Information :</heading>
          <div className="steps" > {stepChange} step 1 </div>

        </div>
       
          <label>Email :*</label>
          <input
            type="email"
            placeholder=" Email Id"
            id="email "
            value={formValues.email}
            onChange={handelChange}
          />

          <label>Username :*</label>
          <input
            type="text"
            placeholder="User Name "
            id="username"
            value={formValues.username}
            onChange={handelChange}
          />

          <label>Password :*</label>
          <input
            type="Password"
            placeholder="Password "
            id="password"
            value={formValues.password}
            onChange={handelChange}
          />

          <label>Confirm Password:*</label>
          <input
            type="password"
            placeholder="Confirm Password "
            id="confirmPassword"
            value={formValues.confirmpassword}
            onChange={handelChange}
          />
        </form>
        <div class="next_btn">
          <button class="btn1 btn-primry">Previos</button>
          <button class="btn2 btn-primry" onClick={Next_step}> Next </button>
        </div>
      </div>
      <Personal_Information/>
      <Image_Upload/>
      <SignUp_Result/>
    </>
  );
}
export default Sign_Account;
