import React, { useState } from 'react'
import "./Sign_Account.css"
 function Sign_Account() {
  const initialValue = {email:"", username:"", password:"", confirmpassword:""};
  const [formValues, setFormValues] = useState(initialValue );
  
  const handelChange =(e)=>{
    // console.log(e.target)
    const {name, value} = e.target;
    setFormValues({...formValues, [name]: value});
    console.log(formValues);
  }
  return (
    <>
        <div className='SignUp_Account'>
                <p> SIGN UP YOUR ACCOUNT<span> Fill all form feild to go to next step</span></p>
                <ul className='UserAccount_step'>
                    <li> Account</li>
                    <li> Personal</li>
                    <li> Image</li>
                    <li>finish</li>
                </ul>
                <form className='Account_Information'>
                    <heading> Account Information :</heading>
               
                    <label>Email :*</label>
                    <input 
                    type="email" 
                    placeholder= " Email Id" 
                    id='email ' 
                     value={formValues.email}
                      onChange= {handelChange(value)}
                     />
                    
                    
                    <label>Username :*</label>
                    <input 
                    type="text" 
                    placeholder= "User Name "
                      id='username'
                       value={formValues.username}
                       onChange={handelChange}
                       />
                 
                   
                    <label>Password :*</label>
                    <input 
                    type="Password" 
                     placeholder= "Password "
                      id='password' 
                      value={formValues.password}
                      onChange={handelChange}
                      />
                    
                   
                    <label>Confirm Password:*</label>
                    <input 
                    type="password" 
                    placeholder= "Confirm Password " 
                    id="confirmPassword" 
                     value={formValues.confirmpassword}
                     onChange={handelChange}
                     />
                      
                </form>
                <div class="next_btn">
                      <button class="btn1 btn-primry">Previos</button>
                       <button class="btn2 btn-primry"> Next </button>

                  </div>
        </div>
    </>
  )
}
export default Sign_Account
