import { useState, useEffect   } from "react";

import Image_Upload from "./Image_Upload";
import Personal_Information from "./Personal_Information";
import SignUp_Result from "./SignUp_Result";
import "./Sign_Account.css";

function Sign_Account() {
  const initialValues = {
    email: "",
    username: "",
    password: "",
    confirmpasswod: "",
  };
  const [formValues, setFormValues] = useState(initialValues);
  const [formErrors, setFormErrors] = useState({});
  const [isSubmit , setIsSubmint] = useState(false);



  const handleChange = (e) => {
    // console.log(e)
    const { name, value } = e.target;
    console.log(e.target);
    console.log(value);
    setFormValues({ ...formValues, [name]: value });

    console.log(formValues);
  };
  const Next_step =(e)=>{
      e.preventDefault();
      setFormErrors(validate(formValues))
