  }
  useEffect (() =>{
    console.log (formErrors);
      if (Object.keys(  formErrors ).length === 0 &&  isSubmit ){
        console.log (formErrors)
      }
  },[formErrors])
  
  const validate =(values)=>{
      const errors ={};
      const regex =  /^[a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(?:\.[a-zA-Z0-9-]+)*$/
      if (!values.Username){
        errors.Email = " Email is required ! "
      }
      if (!values.Email){
        errors.Username = " User Name is required ! "
      }
      if (!values.Password){
        errors.Password = " Passord is required ! "
      }
      if (!values.ConfirmPassword){
        errors.ConfirmPassword = " Confirm passwod is required ! "
      }
       return errors; 
  }

  return (
    <>
      <div className="SignUp_Account">
        <p>
          SIGN UP YOUR ACCOUNT
          <span> Fill all form feild to go to next step</span>
        </p>
        <ul className="UserAccount_step">
          <li> Account</li>
          <li> Personal</li>
          <li> Image</li>
          <li>finish</li>
        </ul>
        <form className="Account_Information" >
          <div className="account_information_style">
            <heading> Account Information :</heading>
            <div className="steps">{} step 1 </div>
          </div>

          <label>Email :*</label>
          <input
            type="email"
            placeholder=" Email Id"
            id="email "
            value={formValues.Email}
            onChange={handleChange}
          />
          <p>{formErrors.Email} </p>
          <label>Username :*</label>
          <input
            type="text"
            placeholder="User Name "
            id="username"
            value={formValues.Username}
            onChange={handleChange}
          />

          <label>Password :*</label>
          <input
            type="Password"
            placeholder="Password "
            id="password"
            value={formValues.Password}
            onChange={handleChange}
          />

          <label>Confirm Password:*</label>
          <input
            type="password"
            placeholder="Confirm Password "
            id="confirmPassword"
            value={formValues.Confirmpasswod}
            onChange={handleChange}
          />
        
        <div class="next_btn">
          {/* <button class="btn1 btn-primry">Previos</button> */}
          <button class="btn2 btn-primry" onClick={Next_step}> Next </button>
        </div>
        <Personal_Information />
      <Image_Upload />
      <SignUp_Result />
      </form>

      </div>
      
    </>
  );
}
export default Sign_Account;
