import { useState } from "react";

import Image_Upload from "./Image_Upload";
import Personal_Information from "./Personal_Information";
import SignUp_Result from "./SignUp_Result";
import "./Sign_Account.css";


function Sign_Account() { 
  const initialValues = {email:"" , username :"", password:"" , confirmpasswod:""}
 const [formValues, setFormValues] = useState(initialValues);
 console.log(formValues);
 const handleChange =(e)=>{
    // console.log(e)
    const {name, value} = e.target;
    console.log(e.target )
    console.log(value)
        setFormValues({ ...formValues , [name]: value});
      
        // console.log(formValues)
  }
  
  return (
    <>
      <div className="SignUp_Account">
        <p>
         
          SIGN UP YOUR ACCOUNT
          <span> Fill all form feild to go to next step</span>
        </p>
        <ul className="UserAccount_step">
          <li> Account</li>
          <li> Personal</li>
          <li> Image</li>
          <li>finish</li>
        </ul>
        <form className="Account_Information">
        <div className="account_information_style">
        <heading> Account Information :</heading>
          <div className="steps" >{} step 1 </div>

        </div>
       
          <label>Email :*</label>
          <input
            type="email"
            placeholder=" EmailId"
            id="email "
            value={formValues. EmailId}
            onChange={handleChange}
          />

          <label>Username :*</label>
          <input
            type="text"
            placeholder="User Name "
            id="username"
           value={formValues.Username}
           onChange={handleChange}

          />

          <label>Password :*</label>
          <input
            type="Password"
            placeholder="Password "
            id="password"
            value={formValues.Password}
            onChange={handleChange}

          />

          <label>Confirm Password:*</label>
          <input
            type="password"
            placeholder="Confirm Password "
            id="confirmPassword"
            value={formValues.confirmpasswod}
            onChange={handleChange}

          />
        </form>
        <div class="next_btn">
          <button class="btn1 btn-primry">Previos</button>
          <button class="btn2 btn-primry"> Next </button>
        </div>
      </div>
      <Personal_Information/>
      <Image_Upload/>
      <SignUp_Result/>
    </>
  );
}
export default Sign_Account;
