import "./App.css";
import { useState } from "react";
import Sign_Account from "./component/Sign_Account";

 function App(){

  // step for state
  const[step, setStep] = useState(1);

  // function going to the next steep
  const nextStep = () => {
    setStep(step + 1);
  };

  // function going to previous step
  const prevStep = () => {
    setStep(step - 1);
  };
  const [formData, setFormData] = useState({
    firstName: "",
    lastName: "",
    age: "",
    email: ""
  })

  const handleInputData = input => e => {
    // input value from the form
    const {value } = e.target;

    //updating for data state taking previous state and then adding new value to create new object
    setFormData(prevState => ({
      ...prevState,
      [input]: value
  }));
  }

   switch(step){

    case 1 :
      return(
        <Sign_Account  nextStep={nextStep} handleFormData={handleInputData} values={formData} />
      )

   }
  return (
    <div className='app' >


    </div>
  );
 }

export default App;
