import React from 'react';
import { useState } from 'react';
import validator from 'validator';
import {form , card, button } from "react-bootstrap";

 function StepThree(nextStep, prevStep , handleFormData, values ) {

   const [error , setError ]= useState(false)
   const submitFormData = (e) => {
    e.preventDefault();
    // console.log(validator.isEmpty(values.uploadimage));

   nextStep();
       
    // checking if value of first name and last name is empty show error else take to step 2
    
    

   }
  return (
    <>
      <div className='main_container'>
        <p className='text'>SIGN UP YOUR ACCOUNT <span> fill all form field to go to next step</span></p>
            <div className='form_steps'>
                <ul>
                    <li>Account </li>
                    <li>Personal </li>
                    <li>Image </li>
                    <li>Finish </li>

                </ul>
            </div>
            <form  onSubmit={submitFormData}>
                <h2>Image Upload</h2>
                <div className='fields'>
                    <label> Upload image</label>
                    <input 
                    type="file"
                    name='uploadimage'
                    placeholder='upload your image'
                    // defaultValue={values.uploadimage}  
                    // onChange={handleFormData3("uploadimage")}

                    />
                </div>
                <div className='fields'>
                    <label> Upload Signature Photo</label>
                    <input 
                    type="file"
                    name='uploadsignature'
                    // placeholder='upload your Signature Photo'
                    // defaultValue={values.input}  
                    // onChange={handleFormData("uploadsignature")}

                    />
                </div>
                <div className="button">
            <button type="submit" className="next_btn  btn1" onClick={()=>nextStep} > Next </button>
            <button   variant="primary"className="next_btn   btn2 " onClick={prevStep}>  previous  </button>
          </div>
            </form>

     </div>
    </>
  )
}
export default  StepThree
