import "./App.css";
import { useState } from "react";
import { Stepper } from "react-form-stepper";
import  { Progress, Space } from 'antd';

import Personal_Information from "./Components/Personal_Information";
import Image_Information from "./Components/Image_Information";
import Result from "./Components/Result";
import Account_Information from "./Components/Account_Information";
import Stepper_code from "./Components/Stepper_code";
function App() {
  const [step, setstep] = useState(1);
  // const [step2 , setstep2] = useState(2);
  console.log(step);
  let stepupdate = step;
  console.log(stepupdate);
  const [formData, setFormData] = useState({
    email: "",
    username: "",
    password: "",
    confirmpassword: "",
  });
  const [FormData2, setFormData2] = useState({
    firstName: "",
    lastName: "",
    contact: "",
    altenatecontact: "",
  });

  // const nextStep2 = ()=>{
  //   setstep2(step2+1)
  // };
  const [state, setstate] = useState(1);
  const nextState = () => {
    setstate(state + 1);
    console.log(state);
    return nextState;
  };

  const nextStep = () => {
    setstep(step + 1);
    console.log(step);
  };
  const prevStep = () => {
    setstep(stepupdate - 1);
    console.log(stepupdate);
  };
  const handleInputData = (input) => (e) => {
    // input value from the form
    const { value } = e.target;
    console.log(value);
    //updating for data state taking previous state and then adding new value to create new object
    setFormData((prevState) => ({
      ...prevState,
      [input]: value,
    }));
  };
  const handleInputData2 = (input) => (e) => {
    // input value from the form
    const { value } = e.target;
    console.log(value);
    //updating for data state taking previous state and then adding new value to create new object
    setFormData2((prevState) => ({
      ...prevState,
      [input]: value,
    }));
  };
  switch (step) {
    case 1:
      return (
        <>
          <Stepper_code currentStep={step} />
          <Progress strokeLinecap="butt" percent={25} />

          <Account_Information
            nextStep={nextStep}
            handleFormData={handleInputData}
            values={formData}
          />
        </>
      );
    case 2:
      return (
        <>
          <Stepper_code currentStep={step} />
          <Progress strokeLinecap="butt" percent={50} />

          <Personal_Information
            nextStep={nextStep}
            prevStep={prevStep}
            handleFormData2={handleInputData2}
            values={FormData2}
            nextState={nextState}
          />
        </>
      );
    case 3:
      return (
        <>
          <Stepper_code currentStep={step} />
          <Progress strokeLinecap="butt" percent={75} />

          <Image_Information nextStep={nextStep} prevStep={prevStep} />
        </>
      );
    case 4:
      return (
        <>
          <Stepper_code currentStep={step} />
          <Progress strokeLinecap="butt" percent={100} />
          <Result values={formData} values2={FormData2} />
        </>
      );
    default:
      return <div className="App"></div>;
  }
}

export default App;
