import React from "react";
import { useState } from "react";
import validator from "validator";
import { Form, card, button } from "react-bootstrap";

function StepThree({ nextStep, prevStep }) {
  let vlaue = "";
  let value = "";
  const fileChange = (event) => {
    vlaue = event.target.value.length;

    value = event.target.value;
  };
  let vlau2 = "";
  let value2 = "";
  const file2Change = (event) => {
    vlau2 = event.target.value.length;
    value2 = event.target.value;
    console.log(vlau2);
    console.log(value2);
  };
  const [filechange, setfilechange] = useState("");

  let [error, setError] = useState("");
  const submitFormData = (e) => {
    e.preventDefault();

    console.log(value);
    if (vlaue < 1) {
      setError("error is here");
      console.log(error);
    } else if (vlau2 < 1) {
      setError("error is here");
    } else {
      nextStep();
    }
  };
  return (
    <>
      <div className="main_container">
        <p className="text">
          SIGN UP YOUR ACCOUNT{" "}
          <span> fill all form field to go to next step</span>
        </p>
        <div className="form_steps">
          <ul>
            <li>Account </li>
            <li>Personal </li>
            <li>Image </li>
            <li>Finish </li>
          </ul>
        </div>
        <form onSubmit={submitFormData}>
          <div className="heading">
            <h2>Image Upload</h2>

            <p>3-4 step</p>
          </div>
          <div className="fields">
            <label> Upload image</label>
            <input type="file" name="uploadimage" onChange={fileChange} />
            <h2>{error} </h2>
          </div>
          <div className="fields">
            <label> Upload Signature Photo</label>
            <input
              type="file"
              name="uploadsignatur"
              placeholder="upload your Signature Photo"
              onChange={file2Change}
            />
            <h2>{error} </h2>
          </div>
          <div className="button">
            <button type="submit" className="next_btn  btn1">    Submit </button>
            <button variant="primary"  className="next_btn   btn2 "  onClick={prevStep}  >   previous </button>
          </div>
        </form>
      </div>
    </>
  );
}
export default StepThree;
