import {useState} from 'react'
import { Form, Card, Button } from "react-bootstrap";
import validator from "validator";

 function StepTwo({nextStep ,handleFormData, values ,prevStep} ) {
    
    const [error, setError] = useState(false);
   
    const submitFormData = (e) => {
        e.preventDefault();
    
         // checking if value of first name and last name is empty show error else take to next step
        if (validator.isEmpty(values.firstname) || validator.isEmpty(values.lastname) || validator.isEmpty(values.contact) || validator.isEmpty(values.altenatecontact)) {
          setError(true);
        }
    
         else {
          nextStep();
        }
        console.log(validator)
      };
  return (
    <>
     <div className='main_container'>
        <p className='text'>SIGN UP YOUR ACCOUNT <span> fill all form field to go to next step</span></p>
            <div className='form_steps'>
                <ul>
                    <li>Account </li>
                    <li>Personal </li>
                    <li>Image </li>
                    <li>Finish </li>

                </ul>
            </div>
      <form onSubmit={submitFormData}>
      <h2> Prsonal Information</h2>
      <div className='fields'>
            <label> First Name:*</label>
            <input 
            type="text"
            name='firstname'
            // defaultValue={values.firstname}

            placeholder='Enter your first name'
            onChange={handleFormData("firstname")}
        
            /> {error ? (
                <Form.Text style={{ color: "red" }}>
                  This is a required field
                </Form.Text>
              ) : (
                " "
              )}

        </div>
        <div className='fields'>
            <label> Last Name:*</label>
            <input 
            type="text"
            name='lastname'
            // defaultValue={values.lastname}

            placeholder='Enter your last Name'
            onChange={handleFormData("lastname")}
            />
            {error ? (
                <Form.Text style={{ color: "red" }}>
                  This is a required field
                </Form.Text>
              ) : (
                " "
              )}
           
        </div>
        <div className='fields'>
            <label> Contact:*</label>
            <input 
            type="number"
            name='contact'
            // defaultValue={values.contact}

            placeholder='Enter your contact'
            onChange={handleFormData("contact")}
            />
            {error ? (
                <Form.Text style={{ color: "red" }}>
                  This is a required field
                </Form.Text>
              ) : (
                " "
              )}
         
        </div>
        <div className='fields'>
            <label> altenate contact:*</label>
            <input 
            type="number"
            name='altenatecontact'
            // defaultValue={values.altenatecontact}

            placeholder='Enter your altenate contact'
            onChange={handleFormData("altenatecontact")}
           
            />
             {error ? (
                <Form.Text style={{ color: "red" }}>
                  This is a required field
                </Form.Text>
              ) : (
                " "
              )}
        </div>
        <div className='button'>
        <button type='submit' className="next_btn  btn1"> Next</button>
        <button type='submit' onClick={prevStep}  className="next_btn   btn2 "> previous</button>
        </div>
      </form>
      </div>
    </>
  )
}
export default StepTwo
